import { Component, OnInit } from '@angular/core';
import { Store} from '@ngrx/store';
import { AppState ,Item } from '../store/app.state';
import { addItem, removeItem, updateItem } from '../store/item.actions';


@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage implements OnInit {

   id!:string;
   name!:string;
list:any=[]
dataArray:any=[]
  constructor(private store: Store<AppState>) {

  }
ngOnInit() {
  this.store.select(state => state.items).subscribe(items => {
    console.log(items);
    this.list=items['entities'];
    this.dataArray=Object.keys(this.list).map(key=>{
      return{
        name:this.list[key]['name'],
        id:this.list[key]['id']
      }
    });
    console.log(this.dataArray)
  });

}
  onSubmit(i:number){
    const item = {
      name: this.name,
      id: this.id
    };
    if(i==1){

      this.store.dispatch(addItem({item}));
    }

    else if(i==2){
      this.store.dispatch(removeItem({id:item.id}));
    }

    else if(i==3){
      this.store.dispatch(updateItem({item}));
    }


  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Blank
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Blank</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-card>
    <ion-item >
      <ion-label>id</ion-label>
      <ion-input type="text" [(ngModel)]="id" name="id" ></ion-input>
    </ion-item>
    <ion-item>
      <ion-label>name</ion-label>
      <ion-input type="text" [(ngModel)]="name" name="name"></ion-input>
    </ion-item>
<ion-button (click)="onSubmit(1)">lol add</ion-button>
<ion-button (click)="onSubmit(2)">lol delete</ion-button>
<ion-button (click)="onSubmit(3)">lol update</ion-button>
  </ion-card>


  <ion-list >
    <ion-item *ngFor="let r of dataArray" >
      {{r.id}} : {{r.name}}
    </ion-item>
  </ion-list>

  <ion-grid>
    <ion-row>
      <ion-col size=""></ion-col>
    </ion-row>
  </ion-grid>
</ion-content>
